import React, { Component } from 'react'
import Header from './Header'
import Footer from './Footer'
import Form from 'react-bootstrap/Form'
import Button from 'react-bootstrap/Button'
import './UploadEmr.css'

class UploadEmr extends Component {

  constructor(props) {
    super(props)
    this.state = {
        permission:false,
        hasError: false,
        mgs: '',
        account: ''
    }
    this.startLoading = this.startLoading.bind(this)
  }

  componentWillMount() {
    this.startLoading()
  }
  startLoading(){
    const {pathname} = this.props.location;
    if(pathname == '/ShowPatientMrc'){
      if(typeof this.props.location.state.account !== undefined &&
        typeof this.props.location.state.patientAccount !== undefined){
          this.setState({patientAccount:this.props.location.state.patientAccount,
            account:this.props.location.state.account})
          }
      }
    }

    handleSubmit(event) {

      //event.preventDefault();
    }

    render() {

        return (
          <main>
            <Header account = {this.state.account} />
              <div className="divForm">
              <Form onSubmit={this.handleSubmit}>
                <Form.Group controlId="formID">
                  <Form.Label>ID</Form.Label>
                  <Form.Control type="text" placeholder="Enter ID" required pattern="^[0-9]+$" />
                  </Form.Group>
                  <Form.Group controlId="formBasicPassword">
                    <Form.Label>Password</Form.Label>
                    <Form.Control type="password" placeholder="Password" />
                    </Form.Group>
                    <Form.Group>
                      
                      <Form.Control type="file" placeholder="Upload File" accept="application/pdf"/> />
                    </Form.Group>
                    <Button id="btnAdd" variant="primary" type="submit"> Add
                    </Button>
                    </Form>

              </div>
              <br/><br/>
            <Footer/>            
          </main>
        );
    }
}
export default UploadEmr;
