import React, { Component } from 'react'
import './ServiceProviderHome.css'
import { BrowserRouter as Router, Switch, Route,Redirect,Link} from "react-router-dom";
import iconUpload from '../images/icon_upload.png'





class ServiceProviderHome extends Component{

    constructor(props) {
        super(props)
        this.state = {
            permissionToPatient:false,
            hasError: false
        }
        this.searchOnClick = this.searchOnClick.bind(this)
      }
    
    searchOnClick(){
        const search = document.getElementById('searchInput')
        if(search !== undefined && search.value !== ''){
            this.setState({ permissionToPatient: true })
            this.setState({permissionAccount: search.value})
        }
        else{
            this.setState({ permissionToPatient : false })
        }
        search.value = ''
        
    }
    
    render() {
        return(
              <div>
                  <h5 style={{textAlign: "center"}}>Searching patient by key</h5>
                  <div class="cover">
                      <div className="divFrom" >     
                          <input type="search" id="searchInput" />
                              <i class="fa fa-search"  
                              onClick={this.searchOnClick}></i>
                      </div>
                  </div>
                  {this.state.permissionToPatient 
                  ?  
                  <Redirect to="/showPatientMrc"  />
               
                  : null}
              </div>
          );
      }
}
export default ServiceProviderHome;
