import React, { Component } from 'react'
import Header from './Header'
import Footer from './Footer'
import Form from 'react-bootstrap/Form'
import Button from 'react-bootstrap/Button'
import Dropdown from 'react-bootstrap/Dropdown'
import ButtonGroup from 'react-bootstrap/ButtonGroup'
import './UploadEmr.css'

class UploadEmr extends Component {

  constructor(props) {
    super(props)
    this.state = {
        permission:false,
        hasError: false,
        account: '',
        emrTypeValue: '222'
    }
    this.startLoading = this.startLoading.bind(this)
    this.handleSelectedEmrType = this.handleSelectedEmrType.bind(this)
  }

  componentWillMount() {
    this.startLoading()
  }
  startLoading(){
    const {pathname} = this.props.location;
    if(pathname == '/ShowPatientMrc'){
      if(typeof this.props.location.state.account !== undefined &&
        typeof this.props.location.state.patientAccount !== undefined){
          this.setState({patientAccount:this.props.location.state.patientAccount,
            account:this.props.location.state.account})
          }
      }
    }

    handleSubmit(event) {

      //event.preventDefault();
    }
    handleSelectedEmrType(event){
      const number = event.toString().split(",")[0]
      const value = event.toString().split(",")[1]
      this.setState({emrTypeValue: value})
   
    }

    render() {

        return (
          <main>
            <Header account = {this.state.account} />
              <div className="divForm">
              <Form onSubmit={this.handleSubmit}>
                <Form.Group controlId="formID">
                  <Form.Label>ID</Form.Label>
                  <Form.Control id="emrId"type="text" placeholder="Enter ID" required pattern="^[0-9]+$" />
                  </Form.Group>
                  <Dropdown as={ButtonGroup} onSelect={this.handleSelectedEmrType} >
                    <Button variant="upEmrSelect">EMR type</Button>
                    <Dropdown.Toggle split variant="upEmrSelectItems" id="emrType"  />
                    <Dropdown.Menu >
                      <Dropdown.Item id="item0" eventKey="0,LABORATORY TEST RESULTS" >LABORATORY TEST RESULTS</Dropdown.Item>
                      <Dropdown.Item id="item1" eventKey="1" value="REFERENCES">REFERENCES</Dropdown.Item>
                      <Dropdown.Item id="item2"eventKey="2" value="MEDICATIONS AND PRESCRIPTIONS">MEDICATIONS AND PRESCRIPTIONS</Dropdown.Item>
                      <Dropdown.Item id="item3" eventKey="3" value="IMAGING TEST RESULTS">IMAGING TEST RESULTS</Dropdown.Item>
                      <Dropdown.Item id="item4" eventKey="4" value="YOUR DIAGNOSES">YOUR DIAGNOSES</Dropdown.Item>
                      <Dropdown.Item id="item5" eventKey="5" value="YOUR SENSITIVITY">YOUR SENSITIVITY</Dropdown.Item>
                      <Dropdown.Item id="item6" eventKey="6" value="THE VACCINES YOU DID">THE VACCINES YOU DID</Dropdown.Item>
                      <Dropdown.Item id="item7" eventKey="7" value="MEDICAL RECOMMENDATIONS">MEDICAL RECOMMENDATIONS</Dropdown.Item>
                      <Dropdown.Item id="item8" eventKey="8" value="CERTIFICATES OF ILLNESS">CERTIFICATES OF ILLNESS</Dropdown.Item>
                      <Dropdown.Item id="item9" eventKey="9" value="MEDICAL INFORMATION SUMMARY">MEDICAL INFORMATION SUMMARY</Dropdown.Item>
                    </Dropdown.Menu>
                    <br/>
                    <Form.Label id="selectedValue" style={{color:'black'}}>
                      {this.state.emrTypeValue}</Form.Label>
                    </Dropdown>
                    <Form.Group>
                      <br/>
                    <Form.Label>Add file</Form.Label>
                      <Form.Control id="emrFile" type="file" placeholder="Upload File" accept="application/pdf" />
                    </Form.Group>
                    <Button id="btnAdd" variant="upEmr" type="submit"> Add
                    </Button>
                    </Form>

              </div>
              <br/><br/>
            <Footer/>            
          </main>
        );
    }
}
export default UploadEmr;
