import React, { Component } from 'react'
import Header from './Header'
import Footer from './Footer'
import Form from 'react-bootstrap/Form'
import Button from 'react-bootstrap/Button'
import Dropdown from 'react-bootstrap/Dropdown'
import ButtonGroup from 'react-bootstrap/ButtonGroup'
import './UploadEmr.css'

class UploadEmr extends Component {

  constructor(props) {
    super(props)
    this.state = {
        permission:false,
        hasError: false,
        mgs: '',
        account: ''
    }
    this.startLoading = this.startLoading.bind(this)
  }

  componentWillMount() {
    this.startLoading()
  }
  startLoading(){
    const {pathname} = this.props.location;
    if(pathname == '/ShowPatientMrc'){
      if(typeof this.props.location.state.account !== undefined &&
        typeof this.props.location.state.patientAccount !== undefined){
          this.setState({patientAccount:this.props.location.state.patientAccount,
            account:this.props.location.state.account})
          }
      }
    }

    handleSubmit(event) {

      //event.preventDefault();
    }

    render() {

        return (
          <main>
            <Header account = {this.state.account} />
              <div className="divForm">
              <Form onSubmit={this.handleSubmit}>
                <Form.Group controlId="formID">
                  <Form.Label>ID</Form.Label>
                  <Form.Control id="emrId"type="text" placeholder="Enter ID" required pattern="^[0-9]+$" />
                  </Form.Group>
                  <Dropdown as={ButtonGroup} onSelect >
                    <Button variant="upEmrSelect">EMR type</Button>
                    <Dropdown.Toggle split variant="upEmrSelectItems" id="emrType" />
                    <Dropdown.Menu>
                      <Dropdown.Item >LABORATORY TEST RESULTS</Dropdown.Item>
                      <Dropdown.Item >REFERENCES</Dropdown.Item>
                      <Dropdown.Item >MEDICATIONS AND PRESCRIPTIONS</Dropdown.Item>
                      <Dropdown.Item >IMAGING TEST RESULTS</Dropdown.Item>
                      <Dropdown.Item >YOUR DIAGNOSES</Dropdown.Item>
                      <Dropdown.Item >YOUR SENSITIVITY</Dropdown.Item>
                      <Dropdown.Item >THE VACCINES YOU DID</Dropdown.Item>
                      <Dropdown.Item >MEDICAL RECOMMENDATIONS</Dropdown.Item>
                      <Dropdown.Item >CERTIFICATES OF ILLNESS</Dropdown.Item>
                      <Dropdown.Item >MEDICAL INFORMATION SUMMARY</Dropdown.Item>
                    </Dropdown.Menu>
                    <Form.Label id="selectitem" variant="upEmrSelectValuse"></Form.Label>
                    </Dropdown>
                    <Form.Group>
                      <br/>
                    <Form.Label>Add file</Form.Label>
                      <Form.Control id="emrFile" type="file" placeholder="Upload File" accept="application/pdf" />
                    </Form.Group>
                    <Button id="btnAdd" variant="upEmr" type="submit"> Add
                    </Button>
                    </Form>

              </div>
              <br/><br/>
            <Footer/>            
          </main>
        );
    }
}
export default UploadEmr;
