import React, { Component } from 'react'
import './ServiceProviderHome.css'
import { Redirect} from "react-router-dom";
import Header from './Header';
import Footer from './Footer'
import Error from './Error'

class ServiceProviderHome extends Component{

    constructor(props) {
        super(props)
        this.state = {
      //      account: '',
            permission:false,
            patientAccount: '',
            hasError: false
        }
        this.startLoading = this.startLoading.bind(this)
        this.searchOnClick = this.searchOnClick.bind(this)
      }

    componentWillMount() {
        this.startLoading()
    }
    startLoading(){
        console.log(this.props)
        const {state} = this.props.location
        console.log(state)
        if(state !== undefined){
            this.setState({account:state.account})
            //ServiceProviderHome.account = state.accout 
        }
    }


    searchOnClick(){
        const search = document.getElementById('searchInput')
        // check if have permission 
        if(search !== undefined && search.value !== '' ){
            this.setState({ permission: true })
            this.setState({patientAccount: search.value})
        }
        else{
            this.setState({ permission: false })
            window.alert("Sorry you don't have permission!")
        }
        search.value = ''

    }
    render() {
        return(
            <main>
                <Error>
                <Header />
                <br/><br/><br/>
                <h2 style={{paddingLeft:"40px"}}>{this.state.account}</h2>
              <div>
                  <h5 style={{textAlign: "center"}}>Searching patient by key</h5>
                  <div class="cover">
                      <div className="divFrom" >     
                          <input type="search" id="searchInput" />
                              <i class="fa fa-search"  
                              onClick={this.searchOnClick}></i>
                      </div>
                  </div>
                  {this.state.permission 
                  ?  
                  <Redirect push to={{
                    pathname: "/ShowPatientMrc",
                    state: { patientAccount: this.state.patientAccount,
                    account: this.state.account }
                  }} />          
                  : null}
              </div>
              <Footer/>
              </Error>
              </main>
          );
      }
}
export default ServiceProviderHome;
