import React, { Component, useState } from 'react'
import './App.css'
import {Redirect} from "react-router-dom";
import Error from './Error'
import {saveToLocalStorage} from '../storage'
import {loadWeb3, loadBlockchainData} from '../loadBlockchain'
import Form from 'react-bootstrap/Form'
import Button from 'react-bootstrap/Button';
import InputGroup from 'react-bootstrap/InputGroup'
import Header from './Header'
import Footer from './Footer'


class App extends Component {

  async componentDidMount() {
    // loading blockchain 
    await loadWeb3()
    const blockchain = await loadBlockchainData()
    if(blockchain !== undefined ||blockchain !== null){
      this.permissions = blockchain.permissions // save smart contruct
      this.setState({account:blockchain.account,loading:false})
      saveToLocalStorage(this.state) // save state to storage
    }
  }

  constructor(props) {
    super(props)
    this.state = {
      login: false, // show login if false
      serviceProvider: null, //serviceProvider= true login has service povider. flase = login has patient, null=no login 
      loading:true // upalod web3 
    }
    this.permissions = null;
  }

  render() {
    return (
    <>
      <Error>
        {this.state.loading ?<div>Loding.....</div> 
        :
          this.state.login ? 
          this.state.serviceProvider !== null && this.state.serviceProvider 
          ?  
          <Redirect push to={{
             pathname: "/ServiceProviderHome"
            }} />  
           : 
           <Redirect push to={{
              pathname: "/PatientHome"
            }} /> 
          :
          <>
          <Header />
          <br/>
          <Login />
          <br/><br/>
          <Footer />
          </>
        }
      </Error>
    </>
    );
  }
}


function Login(){
  const [privateKey,setPrivateKey] = useState('')
  const [account, setAccount] = useState('')
  const [msgError, setMsgError] = useState('')

  const handleChangeAccount = (event) =>{setAccount(event.target.value)}
  const handleChangePrivateKey = (event) =>{setPrivateKey(event.target.value)}

  const handleSubmit = (event) => {
    event.preventDefault()
    setMsgError('')
  }
  return (

    <div style={{paddingLeft:"30%", paddingRight:"30%"}}>

      <Form method='GET' onSubmit={handleSubmit}>
         <Form.Label style={{fontSize:'15px', color:'red'}}>{msgError}</Form.Label>
         <Form.Control id='inputAccount' value={account} 
            onChange={handleChangeAccount} type="text" placeholder="Enter account address" required
            onInvalid={(event) =>  event.target.setCustomValidity("Account address is required")}/>
          <br/>
         <Form.Control id='inputPrivateKey' value={privateKey} 
            onChange={handleChangePrivateKey} type="password" placeholder="Enter private key" required
            onInvalid={(event) =>  event.target.setCustomValidity("Private key is required")}/>
         <br/>
          <InputGroup className='mb-3'>
           <InputGroup.Prepend>
             <InputGroup.Radio  name="group1"/>
             <InputGroup.Text >Login as a patient</InputGroup.Text>
           </InputGroup.Prepend>
           <FormControl/>
          </InputGroup> 
          <InputGroup className='mb-3'>
           <InputGroup.Prepend>
            <InputGroup.Radio  name="group1"/>
            <InputGroup.Text >New York</InputGroup.Text>
           </InputGroup.Prepend>
           <FormControl/>
           </InputGroup> 
         <br/>
         <Button type='submit' variant='submit'>Sign in</Button>        
      </Form>

    </div>

  );
}
export default App;
