import React, { Component,useState } from 'react'
import Footer from './Footer'
import Header from './Header'
import Form from 'react-bootstrap/Form'
import Modal from 'react-bootstrap/Modal'
import Button from 'react-bootstrap/Button'
import Mrc from './Mrc'
import Web3 from 'web3'
import Permissions from '../abis/Permissions.json'

class PatientHome extends Component{
    
    async componentWillMount() {
        await this.loadWeb3()
        await this.loadBlockchainData()
      }
    async loadWeb3() {
        if (window.ethereum) {
            window.web3 = new Web3(window.ethereum)
            await window.ethereum.enable()
        }
    
        else if (window.web3) {
          window.web3 = new Web3(window.web3.currentProvider)
        }
        else {
          window.alert('Non-Ethereum browser detected. You should consider trying MetaMask!')
        }
      }
    
      async loadBlockchainData() {
        const web3 = window.web3
        const networkId = await web3.eth.net.getId()
        const networkData = Permissions.networks[networkId]
        if(networkData) {
          const permissions = web3.eth.Contract(Permissions.abi, networkData.address)
          this.setState({ permissions })
          const mrc = await this.state.permissions.methods.getMrc(this.state.account).send({ from: this.state.account })
          this.setState({mrc})
        } else {
          window.alert('MrC contract not deployed to detected network.')
        }
      }

    constructor(props) {
        super(props)
        this.state = {
            account: localStorage.getItem('account'),
            hasError: false
        }
    }

    

    render(){
        return(
            <>
                <Header account={this.state.account}/>
                <br/><br/><br/>
                <h4 style={{paddingLeft:"40px",color:"#ff9900"}}>Hello {this.state.account}</h4>
                <br/>
                <Mrc mrc= {this.state.mrc}/>
                <br/><br/>
                <div style={{paddingLeft:"40px"}}>
                    <b>Access to service providers</b>
                    <AccessToServiceProvider />
                </div>
                <br/><br/>
                <Footer />
            </>
        );
    }
}

function AccessToServiceProvider() {
  const [show, setShow,address] = useState(false);

  const handleClose = (event) =>{ 
      setShow(false);
      console.log(event)
  }
  const handleShow = () => setShow(true);

  return (
    <>
      <Form.Label style={{color:"blue"}} onClick={handleShow}>
        Add a new access
      </Form.Label>
      <br/>
      <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>Add access</Modal.Title>
        </Modal.Header>
        <Modal.Body>
         <Form.Group controlId="addressInput">
             <Form.Label>Address</Form.Label>
             <Form.Control id="addressInput" type="text" placeholder="Enter address" required />
         </Form.Group>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
          <Button variant="primary" onClick={handleClose} >
            Save Changes
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
}

export default PatientHome;
