import React, { Component } from 'react'
import Error from './Error'

class Mrc extends Component {
  async componentDidMount() {
    console.log(this.state.mrc)
  }

  constructor(props) {
    super(props)
    this.state = {
      mrc: this.props.mrc,
      loading:false,
      accountShow: this.props.accountShow,
      keyAccount: this.props.accountKey,
      hasError: false
    }
  }

    render(){
        return( 
        <>
        <Error>
          {this.state.mrc.length === 0 
          ? <div className="text-center" style={{color:"orange", fontSize: "30px"}} ><b>MrC is empty!</b></div>
          :
          this.state.loading ? <div>Loading... </div>:
          <Table keyField='id' data={ this.state.mrc } columns={ this.state.columns }></Table>
    }

<br/><br/>
            </Error>
            </>
        );
    }
}
export default Mrc;
